def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    from collections import defaultdict
    subject_groups = defaultdict(list)
    for triple in triples:
        subject_groups[triple.subject].append(triple)

    sentences = []
    for subject, triples_list in subject_groups.items():
        parts = []
        # Group related predicates for better sentence structure
        city_served = None
        country = None
        capital = None
        elevation_meters = None
        elevation_feet = None
        location = None
        operating_organisation = None
        runway_length = None
        runway_name = None
        is_part_of = None
        icao_id = None
        iata_id = None
        native_name = None
        owner = None
        largest_city = None
        administrative_arrondissement = None
        official_language = None
        region_served = None
        leader_party = None
        language = None

        for triple in triples_list:
            if triple.predicate == "cityServed":
                city_served = triple.object
            elif triple.predicate == "country":
                country = triple.object
            elif triple.predicate == "capital":
                capital = triple.object
            elif triple.predicate == "elevationAboveTheSeaLevel":
                elevation_meters = f"{triple.object} meters"
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                elevation_feet = f"{triple.object} feet"
            elif triple.predicate == "location":
                location = triple.object
            elif triple.predicate == "operatingOrganisation":
                operating_organisation = triple.object
            elif triple.predicate == "runwayLength":
                runway_length = f"{triple.object} meters"
            elif triple.predicate == "runwayName":
                runway_name = triple.object
            elif triple.predicate == "isPartOf":
                is_part_of = triple.object
            elif triple.predicate == "icaoLocationIdentifier":
                icao_id = triple.object
            elif triple.predicate == "iataLocationIdentifier":
                iata_id = triple.object
            elif triple.predicate == "nativeName":
                native_name = triple.object
            elif triple.predicate == "owner":
                owner = triple.object
            elif triple.predicate == "largestCity":
                largest_city = triple.object
            elif triple.predicate == "administrativeArrondissement":
                administrative_arrondissement = triple.object
            elif triple.predicate == "officialLanguage":
                official_language = triple.object
            elif triple.predicate == "regionServed":
                region_served = triple.object
            elif triple.predicate == "leaderParty":
                leader_party = triple.object
            elif triple.predicate == "language":
                language = triple.object
            else:
                # Handle any unknown predicates
                parts.append(f"{triple.predicate} {triple.object}")

        # Build the core sentence with logical flow
        sentence_parts = []

        # Start with subject and basic info
        if city_served:
            sentence_parts.append(f"serves the city of {city_served}")
        if location:
            sentence_parts.append(f"located in {location}")
        if country:
            sentence_parts.append(f"located in {country}")
        if capital:
            sentence_parts.append(f"is the capital of {capital}")
        if elevation_meters:
            sentence_parts.append(f"has an elevation of {elevation_meters} above sea level")
        if elevation_feet:
            sentence_parts.append(f"has an elevation of {elevation_feet} above sea level")
        if operating_organisation:
            sentence_parts.append(f"operated by {operating_organisation}")
        if runway_length:
            sentence_parts.append(f"has a runway length of {runway_length}")
        if runway_name:
            sentence_parts.append(f"has a runway named {runway_name}")
        if is_part_of:
            sentence_parts.append(f"is part of {is_part_of}")
        if icao_id:
            sentence_parts.append(f"has ICAO identifier {icao_id}")
        if iata_id:
            sentence_parts.append(f"has IATA identifier {iata_id}")
        if native_name:
            sentence_parts.append(f"also known as {native_name}")
        if owner:
            sentence_parts.append(f"owned by {owner}")
        if largest_city:
            sentence_parts.append(f"the largest city in {largest_city}")
        if administrative_arrondissement:
            sentence_parts.append(f"in the {administrative_arrondissement}")
        if official_language:
            sentence_parts.append(f"has official language {official_language}")
        if region_served:
            sentence_parts.append(f"serves the region of {region_served}")
        if leader_party:
            sentence_parts.append(f"led by the {leader_party}")
        if language:
            sentence_parts.append(f"has official language {language}")

        # Construct the final sentence
        if sentence_parts:
            sentence = f"{subject}"
            if sentence_parts:
                sentence += " " + ", ".join(sentence_parts)
            sentence += "."
            sentences.append(sentence)

    result = " ".join(sentences)
    return result if result else ""

