def predict(triples: list[Triple]) -> str:
    sentence = ""
    if not triples:
        return sentence

    subject = triples[0].subject
    sentence += subject + ", "

    for i, triple in enumerate(triples):
        if i == 0:
            pass
        elif triple.predicate == "cityServed":
            sentence += f"which serves {triple.object}. "
        elif triple.predicate == "country":
            sentence += f"located in {triple.object}. "
        elif triple.predicate == "capital":
            sentence += f"Its capital is {triple.object}. "
        elif triple.predicate == "elevationAboveTheSeaLevel":
            sentence += f"It has an elevation of {triple.object} meters. "
        elif triple.predicate == "location":
            sentence += f"It is located in {triple.object}. "
        elif triple.predicate == "operatingOrganisation":
            sentence += f"It is operated by {triple.object}. "
