    if not triples:
        return sentence

    subject = triples[0].subject
    sentence += subject + ", "

    clauses = []
    for i, triple in enumerate(triples):
        if i == 0:
            continue

        clause = ""
        if triple.predicate == "cityServed":
            clause = f"which serves {triple.object}"
        elif triple.predicate == "country":
            clause = f"located in {triple.object}"
        elif triple.predicate == "capital":
            clause = f"where the capital is {triple.object}"
        elif triple.predicate == "elevationAboveTheSeaLevel":
            clause = f"with an elevation of {triple.object} meters"
        elif triple.predicate == "location":
            clause = f"located in {triple.object}"
        elif triple.predicate == "operatingOrganisation":
            clause = f"operated by {triple.object}"
        elif triple.predicate == "runwayLength":
            clause = f"with a runway length of {triple.object} meters"
        elif triple.predicate == "runwayName":
            clause = f"with runway {triple.object}"
        elif triple.predicate == "isPartOf":
            clause = f"which is part of {triple.object}"
        elif triple.predicate == "1stRunwayLengthFeet":
            clause = f"with its first runway being {triple.object} feet long"
        elif triple.predicate == "1stRunwaySurfaceType":
            clause = f"with its first runway surface type being {triple.object}"
        elif triple.predicate == "3rdRunwayLengthFeet":
            clause = f"and its third runway being {triple.object} feet long"
        elif triple.predicate == "icaoLocationIdentifier":
