            clause = f"with location identifier {triple.object}"
        elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
            clause = f"with an elevation of {triple.object} feet"
        elif triple.predicate == "iataLocationIdentifier":
            clause = f"with IATA identifier {triple.object}"
        elif triple.predicate == "nativeName":
            clause = f"also known as {triple.object}"
        elif triple.predicate == "leaderParty":
            clause = f"led by {triple.object}"
        elif triple.predicate == "language":
            clause = f"where the official language is {triple.object}"
        elif triple.predicate == "owner":
            clause = f"owned by {triple.object}"
        elif triple.predicate == "1stRunwayLengthMetre":
            clause = f"with its first runway being {triple.object} meters long"
