            clause = f"with its fourth runway surface type being {triple.object}"
        elif triple.predicate == "largestCity":
            clause = f"and its largest city being {triple.object}"
        elif triple.predicate == "4thRunwayLengthFeet":
            clause = f"and its fourth runway being {triple.object} feet long"
        elif triple.predicate == "elevationAboveTheSeaLevelInMetres":
            clause = f"with an elevation of {triple.object} meters"
        elif triple.predicate == "administrativeArrondissement":
            clause = f"in the {triple.object}"
        elif triple.predicate == "officialLanguage":
            clause = f"with the official language being {triple.object}"
        elif triple.predicate == "regionServed":
            clause = f"serving the region of {triple.object}"
        else:
            clause = f"which {triple.predicate} {triple.object}"

        clauses.append(clause)

    if clauses:
        sentence += ", " + ", ".join(clauses) + "."

    return sentence

