from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    # Group triples by subject to create more coherent, context-rich sentences
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    sentence_parts = []

    for subject, triples_list in subject_groups.items():
        # Build a descriptive block for each subject
        parts = []
        city_served = None
        country = None
        elevation_meters = None
        elevation_feet = None
        location = None
        operating_organisation = None
        runway_length = None
        runway_name = None
        is_part_of = None
        icao_id = None
        iata_id = None
        native_name = None
        owner = None
        largest_city = None
        capital = None
        official_language = None
        region_served = None

        # Extract and organize information
        for triple in triples_list:
            if triple.predicate == "cityServed":
                city_served = triple.object
            elif triple.predicate == "country":
                country = triple.object
            elif triple.predicate == "elevationAboveTheSeaLevel":
                elevation_meters = triple.object
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                elevation_feet = triple.object
            elif triple.predicate == "location":
                location = triple.object
            elif triple.predicate == "operatingOrganisation":
                operating_organisation = triple.object
            elif triple.predicate == "runwayLength":
                runway_length = triple.object
            elif triple.predicate == "runwayName":
                runway_name = triple.object
            elif triple.predicate == "isPartOf":
                is_part_of = triple.object
            elif triple.predicate == "icaoLocationIdentifier":
                icao_id = triple.object
            elif triple.predicate == "iataLocationIdentifier":
                iata_id = triple.object
            elif triple.predicate == "nativeName":
                native_name = triple.object
            elif triple.predicate == "owner":
                owner = triple.object
            elif triple.predicate == "largestCity":
                largest_city = triple.object
            elif triple.predicate == "capital":
                capital = triple.object
            elif triple.predicate == "officialLanguage":
                official_language = triple.object
            elif triple.predicate == "regionServed":
                region_served = triple.object

        # Build a descriptive sentence for the subject
        if native_name:
            parts.append(f"{subject} ({native_name})")
        else:
            parts.append(subject)

        # Handle city served, capital, or largest city relationships
        if city_served:
            parts.append(f"serves the city of {city_served}")
        elif capital:
            parts.append(f"is the capital of {capital}")
        elif largest_city:
            parts.append(f"includes the largest city {largest_city}")

        # Add geographical context (country or administrative region)
        if country:
            parts.append(f"in {country}")
        elif is_part_of:
            parts.append(f"part of {is_part_of}")

        # Add location details
        if location:
            parts.append(f"located in {location}")

        # Handle elevation with proper unit conversion and clarity
        if elevation_meters and elevation_feet:
            parts.append(f"at an elevation of {elevation_meters} meters ({elevation_feet} feet) above sea level")
        elif elevation_meters:
            parts.append(f"at an elevation of {elevation_meters} meters above sea level")
        elif elevation_feet:
            parts.append(f"at an elevation of {elevation_feet} feet above sea level")

        # Runway information (length and name)
        if runway_length:
            parts.append(f"with a runway length of {runway_length} meters")
        if runway_name:
            parts.append(f"and runway name {runway_name}")

        # Organizational details
        if operating_organisation:
            parts.append(f"operated by {operating_organisation}")
        if owner:
            parts.append(f"owned by {owner}")

        # Identifiers
        if icao_id:
            parts.append(f"ICAO identifier: {icao_id}")
        if iata_id:
            parts.append(f"IATA identifier: {iata_id}")

        # Language and regional context
        if official_language:
            parts.append(f"official language: {official_language}")
        if region_served:
            parts.append(f"serving the region of {region_served}")

        # Combine all parts into a single, coherent sentence
        sentence_parts.append(" ".join(parts) + ".")

    # Join all subject descriptions into one coherent sentence
    # Use a single sentence if possible to maximize sentence count score
    result = " ".join(sentence_parts).strip()
    # Ensure it ends with a period
    if not result.endswith('.'):
        result += '.'
    return result

