from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    if triples:
        sentence += triples[0].subject + " "
        for i, triple in enumerate(triples):
            if i == 0:
                if triple.predicate == "cityServed":
                    sentence += f"serves the city of {triple.object}. "
                elif triple.predicate == "location":
                    sentence += f"is located in {triple.object}. "
                else:
                    sentence += f"is {triple.predicate} {triple.object}. "
            else:
                if triple.predicate == "country":
                    sentence += f"which is in {triple.object}. "
                elif triple.predicate == "isPartOf":
                    sentence += f"which is part of {triple.object}. "
                elif triple.predicate == "language":
                    sentence += f"where {triple.object} is spoken. "
                elif triple.predicate == "capital":
                    sentence += f"where the capital is {triple.object}. "
                else:
                    sentence += f"and is {triple.predicate} {triple.object}. "
    return sentence

