from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    # Group triples by subject to create more coherent, context-rich sentences
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    sentences = []
    for subject, triples_list in subject_groups.items():
        # Use a dictionary to group values by predicate for better context handling
        info = {}
        for triple in triples_list:
            info[triple.predicate] = triple.object

        parts = []

        # Handle city served with contextual flow and proper grammar
        if "cityServed" in info:
            city = info["cityServed"]
            country = info.get("country")
            location = info.get("location")
            arrondissement = info.get("administrativeArrondissement")

            # Build the base sentence with correct subject-verb agreement
            sentence_parts = [f"{subject} serves the city of {city}"]

            # Add location context with proper conjunctions and punctuation
            if country and location:
                sentence_parts.append(f", located in {location}, {country}")
                if arrondissement:
                    sentence_parts.append(f" within the {arrondissement}")
            elif country:
                sentence_parts.append(f", which is in {country}")
                if arrondissement:
                    sentence_parts.append(f" within the {arrondissement}")
            elif location:
                sentence_parts.append(f", located in {location}")
                if arrondissement:
                    sentence_parts.append(f" within the {arrondissement}")
            elif arrondissement:
                sentence_parts.append(f", within the {arrondissement}")

            parts.append("".join(sentence_parts))

        # Handle location and administrative context
        if "location" in info and "cityServed" not in info:
            parts.append(f"located in {info['location']}")
        if "administrativeArrondissement" in info:
            parts.append(f"within the {info['administrativeArrondissement']}")

        # Handle country only if not already covered
        if "country" in info and "cityServed" not in info and "location" not in info:
            parts.append(f"located in {info['country']}")

        # Handle elevation with consistent unit preference: meters > feet
        elevation_meters = info.get("elevationAboveTheSeaLevel")
        elevation_metres = info.get("elevationAboveTheSeaLevelInMetres")
        elevation_feet = info.get("elevationAboveTheSeaLevelInFeet")

        # Prefer meters if available, otherwise use feet
        if elevation_meters is not None:
            parts.append(f"with an elevation of {elevation_meters} meters above sea level")
        elif elevation_metres is not None:
            parts.append(f"with an elevation of {elevation_metres} meters above sea level")
        elif elevation_feet is not None:
            parts.append(f"with an elevation of {elevation_feet} feet above sea level")
        # Handle case where elevation is provided in both meters and feet (e.g., for clarity)
        elif elevation_meters is not None and elevation_feet is not None:
            parts.append(f"with an elevation of {elevation_meters} meters ({elevation_feet} feet) above sea level")

        # Handle operators and ownership
        if "operatingOrganisation" in info:
            parts.append(f"operated by {info['operatingOrganisation']}")
        if "owner" in info:
            parts.append(f"owned by {info['owner']}")

        # Handle runway details
        if "runwayLength" in info:
            parts.append(f"with a runway length of {info['runwayLength']} meters")
        if "runwayName" in info:
            parts.append(f"and runway name {info['runwayName']}")

        # Handle identifiers
        identifiers = []
        if "icaoLocationIdentifier" in info:
            identifiers.append(f"ICAO {info['icaoLocationIdentifier']}")
        if "iataLocationIdentifier" in info:
            identifiers.append(f"IATA {info['iataLocationIdentifier']}")
        if "locationIdentifier" in info:
            identifiers.append(f"location ID {info['locationIdentifier']}")
        if identifiers:
            parts.append(f"with identifiers: {', '.join(identifiers)}")

        # Handle other common attributes
        if "nativeName" in info:
            parts.append(f"also known as {info['nativeName']}")
        if "officialLanguage" in info:
            parts.append(f"where the official language is {info['officialLanguage']}")
        if "largestCity" in info:
            parts.append(f"with {info['largestCity']} as its largest city")
        if "regionServed" in info:
            parts.append(f"serving the region of {info['regionServed']}")

        # Combine parts into a coherent sentence with proper punctuation and flow
        if parts:
            sentence = ", ".join(parts)
            if not sentence.endswith("."):
                sentence += "."
            sentences.append(sentence)

    # Final output: join all sentences with proper spacing and punctuation
    result = " ".join(sentences)
    if result and not result.endswith("."):
        result += "."
    return result

