from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    subject = None
    related_info = []

    for triple in triples:
        if subject is None:
            subject = triple.subject
            if triple.predicate == "cityServed":
                sentence += f"{triple.subject} serves the city of {triple.object}. "
            elif triple.predicate == "country":
                sentence += f"{triple.subject} is located in {triple.object}. "
            elif triple.predicate == "capital":
                sentence += f"{triple.subject} is the capital of {triple.object}. "
            else:
                sentence += f"{triple.subject} {triple.predicate} {triple.object}. "
        else:
            if triple.subject == subject:
                related_info.append((triple.predicate, triple.object))

    if related_info:
        conjunctions = ["and", "also", "furthermore"]
        conjunction_index = 0
        sentence += ", " + conjunctions[conjunction_index % len(conjunctions)]
        for i, (predicate, obj) in enumerate(related_info):
            if predicate == "elevationAboveTheSeaLevel":
                sentence += f"has an elevation of {obj} meters above sea level"
            elif predicate == "location":
                sentence += f"is located in {obj}"
            elif predicate == "operatingOrganisation":
                sentence += f"is operated by {obj}"
            elif predicate == "runwayLength":
                sentence += f"has a runway length of {obj} meters"
            elif predicate == "runwayName":
                sentence += f"has a runway name of {obj}"
            elif predicate == "1stRunwayLengthFeet":
                sentence += f"has a first runway of {obj} feet long"
            elif predicate == "1stRunwaySurfaceType":
                sentence += f"has a first runway surface type of {obj}"
            elif predicate == "icaoLocationIdentifier":
                sentence += f"has an ICAO identifier of {obj}"
            elif predicate == "iataLocationIdentifier":
                sentence += f"has an IATA identifier of {obj}"
            elif predicate == "nativeName":
                sentence += f"is also known as {obj}"
            elif predicate == "leaderParty":
                sentence += f"is led by {obj}"
            elif predicate == "language":
                sentence += f"has an official language of {obj}"
            elif predicate == "owner":
                sentence += f"is owned by {obj}"
            elif predicate == "1stRunwayLengthMetre":
                sentence += f"has a first runway length of {obj} meters"
            elif predicate == "4thRunwaySurfaceType":
                sentence += f"has a fourth runway surface type of {obj}"
            elif predicate == "largestCity":
                sentence += f"is the largest city in {obj}"
            elif predicate == "4thRunwayLengthFeet":
                sentence += f"has a fourth runway length of {obj} feet"
            elif predicate == "elevationAboveTheSeaLevelInMetres":
                sentence += f"has an elevation of {obj} meters above sea level"
            elif predicate == "administrativeArrondissement":
                sentence += f"is in {obj}"
            elif predicate == "officialLanguage":
                sentence += f"has an official language of {obj}"
            elif predicate == "regionServed":
                sentence += f"serves the region of {obj}"
            else:
                sentence += f"{predicate} is {obj}"
            if i < len(related_info) - 1:
                sentence += ", "
        sentence += "."

    return sentence

