from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """Generates a coherent, contextually relevant sentence from a list of triples."""
    sentence = ""
    subject = ""
    for i, triple in enumerate(triples):
        if i == 0:
            subject = triple.subject
            if triple.predicate == "cityServed":
                sentence += f"{subject} serves the city of {triple.object}."
            elif triple.predicate == "country":
                sentence += f"{subject} is located in {triple.object}."
            elif triple.predicate == "capital":
                sentence += f"{subject}'s capital is {triple.object}."
            else:
                sentence += f"{subject} is a {triple.predicate}."
        else:
            if triple.predicate == "cityServed":
                sentence += f", serving {triple.object}"
            elif triple.predicate == "country":
                sentence += f", which is in {triple.object}"
            elif triple.predicate == "elevationAboveTheSeaLevel":
                sentence += f", with an elevation of {triple.object} meters"
            elif triple.predicate == "location":
                sentence += f", located in {triple.object}"
            elif triple.predicate == "runwayLength":
                sentence += f", with a runway length of {triple.object} meters"
            elif triple.predicate == "1stRunwayLengthFeet":
                sentence += f", having a first runway length of {triple.object} feet"
            elif triple.predicate == "1stRunwaySurfaceType":
                sentence += f", with a first runway surface of {triple.object}"
            elif triple.predicate == "iataLocationIdentifier":
                sentence += f", identified as {triple.object}"
            elif triple.predicate == "nativeName":
                sentence += f", also known as {triple.object}"
            elif triple.predicate == "officialLanguage":
                sentence += f", with the official language of {triple.object}"
            else:
                sentence += f", which is a {triple.predicate}"

    return sentence.capitalize() + "."

