from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to build coherent sentences
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    sentences = []
    for subject, triples_list in subject_groups.items():
        parts = []

        # Create a dictionary for quick lookup
        info_dict = {}
        for triple in triples_list:
            info_dict[triple.predicate] = triple.object

        # Handle city served, capital, and largest city relationships
        if "cityServed" in info_dict:
            parts.append(f"serves the city of {info_dict['cityServed']}")
        elif "capital" in info_dict:
            parts.append(f"is the capital of {info_dict['capital']}")
        elif "largestCity" in info_dict:
            parts.append(f"has the largest city {info_dict['largestCity']}")

        # Handle location and geography with proper prepositions and flow
        if "country" in info_dict:
            parts.append(f"in {info_dict['country']}")
        elif "isPartOf" in info_dict:
            parts.append(f"part of {info_dict['isPartOf']}")
        elif "administrativeArrondissement" in info_dict:
            parts.append(f"in the {info_dict['administrativeArrondissement']}")
        elif "location" in info_dict:
            parts.append(f"located in {info_dict['location']}")
        elif "regionServed" in info_dict:
            parts.append(f"serving the region of {info_dict['regionServed']}")

        # Handle elevation with consistent unit handling and dual-unit formatting
        elevation_meters = info_dict.get("elevationAboveTheSeaLevel")
        elevation_feet = info_dict.get("elevationAboveTheSeaLevelInFeet")
        if elevation_meters and elevation_feet:
            parts.append(f"at an elevation of {elevation_meters} meters ({elevation_feet} feet) above sea level")
        elif elevation_meters:
            parts.append(f"at an elevation of {elevation_meters} meters above sea level")
        elif elevation_feet:
            parts.append(f"at an elevation of {elevation_feet} feet above sea level")

        # Handle runway information with logical grouping and natural phrasing
        runway_parts = []
        # Handle primary runway info (1stRunway*)
        if "1stRunwayLengthFeet" in info_dict:
            length = info_dict["1stRunwayLengthFeet"]
            if "1stRunwayLengthMetre" in info_dict:
                length = f"{length} feet ({info_dict['1stRunwayLengthMetre']} meters)"
            runway_parts.append(f"runway {info_dict.get('1stRunwayNumber', '1')} with a length of {length}")
        if "1stRunwaySurfaceType" in info_dict:
            runway_parts.append(f"with a {info_dict['1stRunwaySurfaceType']} surface")
        # Handle additional runways
        for i in [2, 3, 4, 5]:
            if f"{i}thRunwayNumber" in info_dict:
                runway_num = info_dict[f"{i}thRunwayNumber"]
                length_key = f"{i}thRunwayLengthFeet"
                surface_key = f"{i}thRunwaySurfaceType"
                parts = [f"runway {runway_num}"]
                if length_key in info_dict:
                    length = info_dict[length_key]
                    if i == 3 and f"{i}thRunwayLengthMetre" in info_dict:
                        length = f"{length} feet ({info_dict[f'{i}thRunwayLengthMetre']} meters)"
                    parts.append(f"with a length of {length}")
                if surface_key in info_dict:
                    parts.append(f"with a {info_dict[surface_key]} surface")
                runway_parts.append(" ".join(parts))
        # Handle general runway info
        if "runwayLength" in info_dict:
            runway_parts.append(f"with a runway length of {info_dict['runwayLength']} meters")
        if "runwayName" in info_dict:
            runway_parts.append(f"named {info_dict['runwayName']}")
        if "runwaySurfaceType" in info_dict:
            runway_parts.append(f"with a {info_dict['runwaySurfaceType']} surface")
        if runway_parts:
            parts.append(f"with runways: {', '.join(runway_parts)}")

        # Handle operating organisation and ownership
        if "operatingOrganisation" in info_dict:
            parts.append(f"operated by {info_dict['operatingOrganisation']}")
        if "owner" in info_dict:
            parts.append(f"owned by {info_dict['owner']}")

        # Handle identifiers with consistent formatting
        identifiers = []
        if "icaoLocationIdentifier" in info_dict:
            identifiers.append(f"ICAO {info_dict['icaoLocationIdentifier']}")
        if "iataLocationIdentifier" in info_dict:
            identifiers.append(f"IATA {info_dict['iataLocationIdentifier']}")
        if "locationIdentifier" in info_dict:
            identifiers.append(f"Location ID {info_dict['locationIdentifier']}")
        if identifiers:
            parts.append(f"with identifiers: {', '.join(identifiers)}")

        # Handle names and languages with improved clarity
        if "nativeName" in info_dict:
            parts.append(f"also known as {info_dict['nativeName']}")
        if "officialLanguage" in info_dict:
            parts.append(f"where the official language is {info_dict['officialLanguage']}")
        if "language" in info_dict:
            parts.append(f"where the official language is {info_dict['language']}")

        # Construct final sentence
        if not parts:
            sentences.append(f"{subject}.")
        elif len(parts) == 1:
            sentences.append(f"{subject} {parts[0]}.")
        else:
            # Join with commas and 'and' for natural flow
            joined_parts = ", ".join(parts[:-1]) + f" and {parts[-1]}"
            sentences.append(f"{subject} {joined_parts}.")

    return " ".join(sentences)

