from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    for i, triple in enumerate(triples):
        if triple.predicate in ["cityServed", "country", "location", "operatingOrganisation", "isPartOf", "iataLocationIdentifier", "nativeName", "language", "owner", "regionServed"]:
            sentence += f"{triple.subject} {triple.predicate} {triple.object}. "
        elif triple.predicate == "capital":
            if i > 0:
                sentence += " and "
            sentence += f"the capital of {triple.subject} is {triple.object}. "
        elif triple.predicate == "elevationAboveTheSeaLevel":
            sentence += f"{triple.subject} has an elevation of {triple.object} meters above sea level. "
        elif triple.predicate in ["runwayLength", "runwayName", "icaoLocationIdentifier", "locationIdentifier", "elevationAboveTheSeaLevelInFeet", "elevationAboveTheSeaLevelInMetres", "administrativeArrondissement", "officialLanguage"]:
            sentence += f"{triple.subject} {triple.predicate} {triple.object}. "
        elif triple.predicate in ["1stRunwayLengthFeet", "1stRunwaySurfaceType", "3rdRunwayLengthFeet", "4thRunwaySurfaceType", "4thRunwayLengthFeet"]:
            sentence += f"{triple.subject} has a {triple.predicate} of {triple.object}. "
        elif triple.predicate == "1stRunwayLengthMetre":
            sentence += f"{triple.subject} has a first runway length of {triple.object} meters. "
        elif triple.predicate == "largestCity":
            sentence += f"{triple.subject} is the largest city in {triple.object}. "
    return sentence

