from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    location_info = []
    runway_info = []
    other_info = []

    for triple in triples:
        if triple.predicate in ("cityServed", "country", "capital", "location", "isPartOf"):
            location_info.append(triple)
        elif triple.predicate in ("runwayLength", "runwayName", "1stRunwayLengthFeet", "1stRunwaySurfaceType", "1stRunwayLengthMetre", "4thRunwaySurfaceType"):
            runway_info.append(triple)
        else:
            other_info.append(triple)

    if location_info:
        location_str = ""
        for i, triple in enumerate(location_info):
            if triple.predicate == "cityServed":
                location_str += f"{triple.subject} serves the city of {triple.object}"
            elif triple.predicate == "country":
                location_str += f" located in {triple.object}"
            elif triple.predicate == "capital":
                location_str += f", the capital of which is {triple.object}"
            elif triple.predicate == "location":
                location_str += f" in {triple.object}"
            elif triple.predicate == "isPartOf":
                 location_str += f" which is part of {triple.object}"
            if i < len(location_info) - 1:
                location_str += ", "
        sentence += location_str + ". "

    if runway_info:
        runway_str = ""
        for i, triple in enumerate(runway_info):
            if triple.predicate == "runwayLength":
                runway_str += f"with a runway length of {triple.object} meters"
            elif triple.predicate == "runwayName":
                runway_str += f" with runway name {triple.object}"
            elif triple.predicate == "1stRunwayLengthFeet":
                runway_str += f" and a first runway length of {triple.object} feet"
            elif triple.predicate == "1stRunwaySurfaceType":
                runway_str += f" with a first runway surface type of {triple.object}"
            elif triple.predicate == "1stRunwayLengthMetre":
                runway_str += f" and a first runway length of {triple.object} meters"
            elif triple.predicate == "4thRunwaySurfaceType":
                runway_str += f" and a fourth runway surface type of {triple.object}"
            if i < len(runway_info) - 1:
                runway_str += ", "
        sentence += runway_str + ". "

    if other_info:
        for triple in other_info:
            sentence += f"{triple.subject} {triple.predicate} {triple.object}. "

    return sentence

