from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to build coherent sentences
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    sentences = []
    for subject, triples_list in subject_groups.items():
        parts = []

        # Create a dictionary for quick lookup
        info_dict = {}
        for triple in triples_list:
            info_dict[triple.predicate] = triple.object

        # Build descriptive components with proper prioritization
        # Priority 1: City served, capital, and largest city relationships
        if "cityServed" in info_dict:
            parts.append(f"serves the city of {info_dict['cityServed']}")
        elif "capital" in info_dict:
            parts.append(f"is the capital of {info_dict['capital']}")
        elif "largestCity" in info_dict:
            parts.append(f"has the largest city {info_dict['largestCity']}")

        # Priority 2: Location and geography with improved preposition usage
        location_info = []
        if "country" in info_dict:
            location_info.append(f"in {info_dict['country']}")
        elif "isPartOf" in info_dict:
            location_info.append(f"part of {info_dict['isPartOf']}")
        elif "administrativeArrondissement" in info_dict:
            location_info.append(f"in the {info_dict['administrativeArrondissement']}")
        elif "location" in info_dict:
            location_info.append(f"located in {info_dict['location']}")
        elif "regionServed" in info_dict:
            location_info.append(f"serving the region of {info_dict['regionServed']}")

        # Handle nested location relationships (e.g., city within country)
        if location_info and "country" in info_dict:
            # If cityServed is present, ensure it's properly linked
            if "cityServed" in info_dict and len(location_info) > 1:
                # Move country to the end for better flow
                country_part = location_info.pop()
                location_info.append(country_part)
            elif "capital" in info_dict:
                # Capital should be mentioned with its country
                location_info.append(f"in {info_dict['country']}")

        if location_info:
            parts.append(", ".join(location_info))

        # Priority 3: Elevation (prioritize metric if both metric and feet are present)
        elevation_meters = info_dict.get("elevationAboveTheSeaLevel")
        elevation_feet = info_dict.get("elevationAboveTheSeaLevelInFeet")
        if elevation_meters:
            if elevation_feet:
                parts.append(f"at an elevation of {elevation_meters} meters ({elevation_feet} feet) above sea level")
            else:
                parts.append(f"at an elevation of {elevation_meters} meters above sea level")
        elif elevation_feet:
            parts.append(f"at an elevation of {elevation_feet} feet above sea level")

        # Priority 4: Runway information (group by runway number if multiple exist)
        runway_parts = []
        # Handle specific runway properties
        for i in range(1, 6):
            length_key = f"{i}thRunwayLengthFeet"
            length_metre_key = f"{i}thRunwayLengthMetre"
            surface_key = f"{i}thRunwaySurfaceType"
            number_key = f"{i}thRunwayNumber"

            if length_key in info_dict:
                length = info_dict[length_key]
                unit = "feet"
                runway_parts.append(f"runway {i} is {length} {unit}")
            elif length_metre_key in info_dict:
                length = info_dict[length_metre_key]
                unit = "meters"
                runway_parts.append(f"runway {i} is {length} {unit}")
            elif number_key in info_dict:
                runway_parts.append(f"runway {i} is numbered {info_dict[number_key]}")
            elif surface_key in info_dict:
                runway_parts.append(f"runway {i} has a {info_dict[surface_key]} surface")

        # Handle generic runway info if no numbered runways found
        if not runway_parts and "runwayLength" in info_dict:
            runway_parts.append(f"{info_dict['runwayLength']} meters")
        if not runway_parts and "runwayName" in info_dict:
            runway_parts.append(f"named {info_dict['runwayName']}")
        if not runway_parts and "runwaySurfaceType" in info_dict:
            runway_parts.append(f"with a {info_dict['runwaySurfaceType']} surface")

        # Consolidate runway information into a single descriptive phrase
        if runway_parts:
            # Sort runways by number for consistent order
            sorted_runways = []
            for i in range(1, 6):
                if f"{i}thRunwayNumber" in info_dict:
                    sorted_runways.append(f"runway {i} (numbered {info_dict[f'{i}thRunwayNumber']})")
                elif f"{i}thRunwayLengthFeet" in info_dict or f"{i}thRunwayLengthMetre" in info_dict:
                    sorted_runways.append(f"runway {i}")
            if sorted_runways:
                runway_summary = ", ".join(sorted_runways)
                if "runwaySurfaceType" in info_dict:
                    runway_summary += f" with {info_dict['runwaySurfaceType']} surface"
                if "runwayLength" in info_dict:
                    runway_summary += f" and {info_dict['runwayLength']} meters long"
                parts.append(f"with runways: {runway_summary}")
            else:
                parts.append(f"with runways: {', '.join(runway_parts)}")

        # Priority 5: Operating organisation and ownership
        if "operatingOrganisation" in info_dict:
            parts.append(f"operated by {info_dict['operatingOrganisation']}")
        if "owner" in info_dict:
            parts.append(f"owned by {info_dict['owner']}")

        # Priority 6: Identifiers
        identifiers = []
        if "icaoLocationIdentifier" in info_dict:
            identifiers.append(f"ICAO {info_dict['icaoLocationIdentifier']}")
        if "iataLocationIdentifier" in info_dict:
            identifiers.append(f"IATA {info_dict['iataLocationIdentifier']}")
        if "locationIdentifier" in info_dict:
            identifiers.append(f"Location ID: {info_dict['locationIdentifier']}")
        if identifiers:
            parts.append(f"with identifiers: {', '.join(identifiers)}")

        # Priority 7: Names and languages
        if "nativeName" in info_dict:
            parts.append(f"also known as {info_dict['nativeName']}")
        if "officialLanguage" in info_dict:
            parts.append(f"where the official language is {info_dict['officialLanguage']}")
        if "language" in info_dict:
            parts.append(f"where the official language is {info_dict['language']}")

        # Construct final sentence
        if not parts:
            sentences.append(f"{subject}.")
        elif len(parts) == 1:
            sentences.append(f"{subject} {parts[0]}.")
        else:
            # Join with commas and 'and' for natural flow
            joined_parts = ", ".join(parts[:-1]) + f" and {parts[-1]}"
            sentences.append(f"{subject} {joined_parts}.")

    return " ".join(sentences)

