                    runway_parts.append(f"{length_metre} meters long")
                if surface:
                    runway_parts.append(f"with a {surface} surface")

                if runway_parts:
                    runway_info.append(" ".join(runway_parts))

        # Deduplicate and reorganize runway info if multiple runways exist
        if runway_info:
            # Use proper English grammar for multiple runways
            if len(runway_info) == 1:
                parts.append(f"with {runway_info[0]}")
            else:
                # Join with commas and "and" for the last item
                runway_str = ", ".join(runway_info[:-1]) + f" and {runway_info[-1]}"
                parts.append(f"with runways: {runway_str}")

        # Consolidate all runway information into a single coherent phrase
