            if len(runway_info) == 1:
                parts.append(f"with {runway_info[0]}")
            else:
                # Join with commas and "and" for the last item
                runway_str = ", ".join(runway_info[:-1]) + f" and {runway_info[-1]}"
                parts.append(f"with runways: {runway_str}")

        # Handle general runway info (non-numbered)
        runway_parts = []
        if "runwayLength" in info:
            runway_parts.append(f"{info['runwayLength']} meters")
        if "runwayName" in info:
            runway_parts.append(f"named {info['runwayName']}")
        if "runwaySurfaceType" in info:
