from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    subject = ""
    for i, triple in enumerate(triples):
        if triple.predicate == "cityServed":
            if not sentence:
                sentence += f"{triple.subject} serves the city of {triple.object}"
            else:
                sentence += f", serving {triple.object}"
        elif triple.predicate == "country":
            if not sentence:
                sentence += f"{triple.subject} is located in {triple.object}"
            else:
                sentence += f", which is in {triple.object}"
        elif triple.predicate == "capital":
            if not sentence:
                sentence += f"{triple.subject} is the capital of {triple.object}"
            else:
                sentence += f", the capital of which is {triple.object}"
        elif triple.predicate == "elevationAboveTheSeaLevel":
            if not sentence:
                sentence += f"{triple.subject} is at an elevation of {triple.object} meters"
            else:
                sentence += f", with an elevation of {triple.object} meters"
        elif triple.predicate == "location":
            if not sentence:
                sentence += f"{triple.subject} is located in {triple.object}"
            else:
                sentence += f", situated in {triple.object}"
        elif triple.predicate == "operatingOrganisation":
            if not sentence:
                sentence += f"{triple.subject} is operated by {triple.object}"
            else:
                sentence += f", and is operated by {triple.object}"
        elif triple.predicate == "runwayLength":
            if not sentence:
                sentence += f"{triple.subject} has a runway length of {triple.object} meters"
            else:
                sentence += f", with a runway length of {triple.object} meters"
        elif triple.predicate == "runwayName":
            if not sentence:
                sentence += f"{triple.subject} has a runway named {triple.object}"
            else:
                sentence += f", and a runway named {triple.object}"
        elif triple.predicate == "isPartOf":
            if not sentence:
                sentence += f"{triple.subject} is part of {triple.object}"
            else:
                sentence += f", and is part of {triple.object}"
        else:
            if i == 0:
                sentence += f"{triple.subject} {triple.predicate} {triple.object}, "
                if triple.predicate in ["cityServed", "country", "capital", "elevationAboveTheSeaLevel", "location", "operatingOrganisation", "runwayLength", "runwayName", "isPartOf"]:
                    sentence = sentence[:-2] + ". "

                # Add clauses for other predicates
                elif triple.predicate in ["1stRunwayLengthFeet", "1stRunwaySurfaceType", "3rdRunwayLengthFeet", "icaoLocationIdentifier", "locationIdentifier", "elevationAboveTheSeaLevelInFeet", "iataLocationIdentifier", "nativeName", "leaderParty", "language", "owner", "1stRunwayLengthMetre", "4thRunwaySurfaceType", "largestCity", "4thRunwayLengthFeet", "elevationAboveTheSeaLevelInMetres", "administrativeArrondissement", "officialLanguage", "regionServed"]:
                    sentence += f"The {triple.predicate.replace('1st', '').replace('3rd', '').replace('4th', '').replace('5th', '')} of {triple.subject} is {triple.object}. "

                elif triple.predicate in ["hubAirport", "aircraftHelicopter", "transportAircraft", "currency", "headquarter", "class", "division", "order", "leaderTitle"]:
                    sentence += f"{triple.subject} {triple.predicate} {triple.object}. "

                elif triple.predicate in ["battle", "aircraftFighter", "attackAircraft"]:
                    sentence += f"{triple.subject} {triple.predicate} the {triple.object}. "

                elif triple.predicate in ["5thRunwaySurfaceType"]:
                    sentence += f"The {triple.predicate.replace('5th', '')} surface type of {triple.subject} is {triple.object}. "

                elif triple.predicate in ["countySeat", "chief", "foundedBy", "postalCode", "areaCode", "foundingYear", "ceremonialCounty"]:
                    sentence += f"{triple.subject} {triple.predicate} {triple.object}. "
            else:
                sentence += f" and {triple.subject} {triple.predicate} {triple.object}"
    return sentence

