                parts.append(f"its runway name is {triple.object}")
            elif triple.predicate == "isPartOf":
                parts.append(f"{subject} is part of {triple.object}")
            elif triple.predicate == "1stRunwayLengthFeet":
                parts.append(f"its first runway is {triple.object} feet long")
            elif triple.predicate == "1stRunwaySurfaceType":
                parts.append(f"its first runway surface type is {triple.object}")
