                parts.append(f"its location identifier is {triple.object}")
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                parts.append(f"{subject} has an elevation of {triple.object} feet above sea level")
            elif triple.predicate == "iataLocationIdentifier":
                parts.append(f"its IATA location identifier is {triple.object}")
            elif triple.predicate == "nativeName":
                parts.append(f"{subject} is also known as {triple.object}")
            elif triple.predicate == "leaderParty":
                parts.append(f"{subject} is led by the {triple.object}")
            elif triple.predicate == "language":
                parts.append(f"the official language of {subject} is {triple.object}")
            elif triple.predicate == "owner":
                parts.append(f"{subject} is owned by {triple.object}")
            elif triple.predicate == "1stRunwayLengthMetre":
                parts.append(f"its first runway is {triple.object} meters long")
            elif triple.predicate == "4thRunwaySurfaceType":
                parts.append(f"its fourth runway surface type is {triple.object}")
            elif triple.predicate == "largestCity":
                parts.append(f"the largest city in {subject} is {triple.object}")
            elif triple.predicate == "4thRunwayLengthFeet":
                parts.append(f"its fourth runway is {triple.object} feet long")
            elif triple.predicate == "elevationAboveTheSeaLevelInMetres":
                parts.append(f"{subject} has an elevation of {triple.object} meters above sea level")
            elif triple.predicate == "administrativeArrondissement":
                parts.append(f"{subject} is in the {triple.object}")
