                parts.append(f"{subject} serves the region of {triple.object}")
            else:
                parts.append(f"{subject} {triple.predicate} {triple.object}")

        # Join parts for this subject with commas and proper punctuation
        sentence_parts.append(", ".join(parts) + ".")

    # Combine all subject sentences into a single coherent sentence
    return " ".join(sentence_parts)

