            clauses.append(f"{subject} has {obj} as its capital")
        elif predicate == "elevationAboveTheSeaLevel":
            clauses.append(f"{subject} has an elevation of {obj} meters")
        elif predicate == "location":
            clauses.append(f"{subject} is located in {obj}")
        elif predicate == "operatingOrganisation":
            clauses.append(f"{subject} is operated by {obj}")
