            clauses.append(f"{subject} has a runway length of {obj} meters")
        elif predicate == "runwayName":
            clauses.append(f"{subject} has a runway named {obj}")
        elif predicate == "isPartOf":
            clauses.append(f"{subject} is part of {obj}")
        elif predicate == "1stRunwayLengthFeet":
            clauses.append(f"{subject}'s first runway is {obj} feet long")
        elif predicate == "1stRunwaySurfaceType":
            clauses.append(f"{subject}'s first runway surface is {obj}")
        elif predicate == "3rdRunwayLengthFeet":
            clauses.append(f"{subject}'s third runway is {obj} feet long")
        elif predicate == "icaoLocationIdentifier":
            clauses.append(f"{subject} has ICAO identifier {obj}")
        elif predicate == "locationIdentifier":
            clauses.append(f"{subject} has location identifier {obj}")
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            clauses.append(f"{subject} has an elevation of {obj} feet")
        elif predicate == "iataLocationIdentifier":
            clauses.append(f"{subject} has IATA identifier {obj}")
        elif predicate == "nativeName":
            clauses.append(f"{subject} is also known as {obj}")
        elif predicate == "leaderParty":
            clauses.append(f"{subject} is led by {obj}")
        elif predicate == "language":
            clauses.append(f"{subject} speaks {obj}")
        elif predicate == "owner":
            clauses.append(f"{subject} is owned by {obj}")
        elif predicate == "1stRunwayLengthMetre":
            clauses.append(f"{subject}'s first runway is {obj} meters long")
        elif predicate == "4thRunwaySurfaceType":
            clauses.append(f"{subject}'s fourth runway surface is {obj}")
        elif predicate == "largestCity":
            clauses.append(f"{subject}'s largest city is {obj}")
        elif predicate == "4thRunwayLengthFeet":
            clauses.append(f"{subject}'s fourth runway is {obj} feet long")
        elif predicate == "elevationAboveTheSeaLevelInMetres":
            clauses.append(f"{subject} has an elevation of {obj} meters")
        elif predicate == "administrativeArrondissement":
            clauses.append(f"{subject} is in the {obj} arrondissement")
        elif predicate == "officialLanguage":
            clauses.append(f"{subject}'s official language is {obj}")
        elif predicate == "regionServed":
            clauses.append(f"{subject} serves the region of {obj}")
        else:
            clauses.append(f"{subject} is a {predicate}")

    sentence = ", ".join(clauses) + "."
    return sentence

