        # Join parts into a single sentence with natural flow
        if len(parts) == 1:
            sentences.append(f"{subject} {parts[0]}.")
        else:
            # Use natural conjunctions and improve flow with relative clauses
            joined_parts = ", ".join(parts[:-1])
            if joined_parts:
                joined_parts += f", and {parts[-1]}"
