from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

def predict(triples: list[Triple]) -> str:
    """
    Converts a list of triples into a coherent sentence about airports.
    """
    sentence = ""
    airport = None
    country = None
    capital = None
    elevation = None
    runway_length = None
    location = None

    for triple in triples:
        if triple.predicate == "cityServed":
            if not sentence:
                sentence += f"{triple.subject} serves the city of {triple.object}."
            else:
                sentence += f", serving {triple.object}."
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "capital":
            capital = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "runwayLength":
            runway_length = triple.object
        elif triple.predicate == "location":
            location = triple.object
        elif triple.predicate == "1stRunwayLengthFeet":
            sentence += f" Its first runway is {triple.object} feet long."
        elif triple.predicate == "1stRunwaySurfaceType":
            sentence += f" The first runway surface is {triple.object}."
        elif triple.predicate == "iataLocationIdentifier":
            sentence += f" Its IATA identifier is {triple.object}."
        elif triple.predicate == "nativeName":
            sentence += f" It is also known as {triple.object}."
        elif triple.predicate == "hubAirport":
            sentence += f" It is a hub for {triple.object}."
        elif triple.predicate == "operatingOrganisation":
            sentence += f" It is operated by {triple.object}."

    if country:
        sentence += f" It is located in {country}."
    if capital and country:
        sentence += f" The capital of {country} is {capital}."
    if elevation:
        sentence += f" It has an elevation of {elevation} meters."
    if runway_length:
        sentence += f" Its runway length is {runway_length} meters."
    if location:
        sentence += f" It is located in {location}."

    return sentence

