from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to build cohesive sentences
    from collections import defaultdict
    subject_groups = defaultdict(list)
    for triple in triples:
        subject_groups[triple.subject].append(triple)

    sentences = []
    for subject, triples_list in subject_groups.items():
        # Collect information for this subject
        info = {}
        for triple in triples_list:
            if triple.predicate not in info:
                info[triple.predicate] = []
            info[triple.predicate].append(triple.object)

        # Build parts for the sentence
        parts = [subject]

        # Handle various predicates with appropriate natural language formatting
        if "cityServed" in info:
            cities = info["cityServed"]
            if cities:
                parts.append(f"serves the city of {cities[0]}")

        if "country" in info:
            countries = info["country"]
            if countries:
                parts.append(f"in {countries[0]}")

        if "capital" in info:
            capitals = info["capital"]
            if capitals:
                parts.append(f"where the capital is {capitals[0]}")

        if "largestCity" in info:
            cities = info["largestCity"]
            if cities:
                parts.append(f"with largest city {cities[0]}")

        if "location" in info:
            locations = info["location"]
            if locations:
                parts.append(f"located in {locations[0]}")

        if "elevationAboveTheSeaLevel" in info:
            elevations = info["elevationAboveTheSeaLevel"]
            if elevations:
                parts.append(f"at an elevation of {elevations[0]} meters above sea level")

        if "elevationAboveTheSeaLevelInFeet" in info:
            elevations = info["elevationAboveTheSeaLevelInFeet"]
            if elevations:
                parts.append(f"at an elevation of {elevations[0]} feet above sea level")

        if "operatingOrganisation" in info:
            orgs = info["operatingOrganisation"]
            if orgs:
                parts.append(f"operated by {orgs[0]}")

        if "runwayLength" in info:
            lengths = info["runwayLength"]
            if lengths:
                parts.append(f"with runway length {lengths[0]} meters")

        if "runwayName" in info:
            names = info["runwayName"]
            if names:
                parts.append(f"named {names[0]}")

        if "isPartOf" in info:
            parts_info = info["isPartOf"]
            if parts_info:
                parts.append(f"part of {parts_info[0]}")

        if "icaoLocationIdentifier" in info:
            identifiers = info["icaoLocationIdentifier"]
            if identifiers:
                parts.append(f"ICAO identifier: {identifiers[0]}")

        if "iataLocationIdentifier" in info:
            identifiers = info["iataLocationIdentifier"]
            if identifiers:
                parts.append(f"IATA identifier: {identifiers[0]}")

        if "nativeName" in info:
            names = info["nativeName"]
            if names:
                parts.append(f"also known as {names[0]}")

        if "owner" in info:
            owners = info["owner"]
            if owners:
                parts.append(f"owned by {owners[0]}")

        if "officialLanguage" in info:
            languages = info["officialLanguage"]
            if languages:
                parts.append(f"where the official language is {languages[0]}")

        if "regionServed" in info:
            regions = info["regionServed"]
            if regions:
                parts.append(f"serving the region of {regions[0]}")

        # Join all parts into a coherent sentence
        sentence = " ".join(parts) + "."
        sentences.append(sentence)

    return " ".join(sentences)

