from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to build coherent, contextually rich sentences
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    sentences = []
    for subject, triples_list in subject_groups.items():
        # Build a dictionary of predicate -> object
        info = {}
        for triple in triples_list:
            info[triple.predicate] = triple.object

        parts = []

        # Handle city served, capital, and largest city with hierarchical logic
        if "cityServed" in info:
            city = info["cityServed"]
            parts.append(f"serves the city of {city}")
            # If the city is also in a country, add that context
            if "country" in info:
                country = info["country"]
                if "capital" not in info:
                    parts.append(f"located in {country}")
        elif "capital" in info:
            parts.append(f"is the capital of {info['capital']}")
        elif "largestCity" in info:
            parts.append(f"has the largest city {info['largestCity']}")

        # Handle location and geography
        if "country" in info:
            parts.append(f"located in {info['country']}")
        elif "isPartOf" in info:
            parts.append(f"part of {info['isPartOf']}")
        elif "administrativeArrondissement" in info:
            parts.append(f"in the {info['administrativeArrondissement']}")
        elif "location" in info:
            parts.append(f"located in {info['location']}")
        elif "regionServed" in info:
            parts.append(f"serving the region of {info['regionServed']}")

        # Handle elevation (both meters and feet)
        elevation_meters = info.get("elevationAboveTheSeaLevel")
        elevation_feet = info.get("elevationAboveTheSeaLevelInFeet")
        elevation_metres = info.get("elevationAboveTheSeaLevelInMetres")
        if elevation_meters and elevation_feet:
            parts.append(f"at an elevation of {elevation_meters} meters ({elevation_feet} feet) above sea level")
        elif elevation_meters:
            parts.append(f"at an elevation of {elevation_meters} meters above sea level")
        elif elevation_feet:
            parts.append(f"at an elevation of {elevation_feet} feet above sea level")
        elif elevation_metres:
            parts.append(f"at an elevation of {elevation_metres} meters above sea level")

        # Handle runway details with unified, non-redundant logic
        runway_sections = []
        runway_numbers = set()
        for i in [1, 2, 3, 4, 5]:
            length_key = f"{i}thRunwayLengthFeet"
            length_m_key = f"{i}thRunwayLengthMetre"
            surface_key = f"{i}thRunwaySurfaceType"
            number_key = f"{i}thRunwayNumber"
            length_feet = info.get(length_key)
            length_metre = info.get(length_m_key)
            surface = info.get(surface_key)
            number = info.get(number_key)

            if not number and not length_feet and not length_metre and not surface:
                continue

            runways = []
            if number:
                runways.append(f"runway {number}")
                runway_numbers.add(number)
            if length_feet:
                runways.append(f"{length_feet} feet long")
            if length_metre:
                runways.append(f"{length_metre} meters long")
            if surface:
                runways.append(f"with a {surface} surface")

            runway_sections.append(" ".join(runways))

        # Handle general runway info (non-numbered)
        general_runway_parts = []
        if "runwayLength" in info:
            length_val = info["runwayLength"]
            if not any(f"{length_val} meters" in section for section in runway_sections):
                general_runway_parts.append(f"{length_val} meters")
        if "runwayName" in info:
            name_val = info["runwayName"]
            if not any(name_val in section for section in runway_sections):
                general_runway_parts.append(f"named {name_val}")
        if "runwaySurfaceType" in info:
            surface_val = info["runwaySurfaceType"]
            if not any(surface_val in section for section in runway_sections):
                general_runway_parts.append(f"with a {surface_val} surface")

        # Consolidate all runway information
        if runway_sections:
            if len(runway_sections) == 1:
                parts.append(f"with {runway_sections[0]}")
            else:
                runway_str = ", ".join(runway_sections[:-1]) + f" and {runway_sections[-1]}"
                parts.append(f"with runways: {runway_str}")
        if general_runway_parts:
            parts.append(" ".join(general_runway_parts))

        # Handle identifiers
        identifiers = []
        if "icaoLocationIdentifier" in info:
            identifiers.append(f"ICAO {info['icaoLocationIdentifier']}")
        if "iataLocationIdentifier" in info:
            identifiers.append(f"IATA {info['iataLocationIdentifier']}")
        if "locationIdentifier" in info:
            identifiers.append(f"location ID {info['locationIdentifier']}")
        if identifiers:
            parts.append(f"with identifiers: {', '.join(identifiers)}")

        # Handle ownership and operation
        if "operatingOrganisation" in info:
            parts.append(f"operated by {info['operatingOrganisation']}")
        if "owner" in info:
            parts.append(f"owned by {info['owner']}")

        # Handle names and languages
        if "nativeName" in info:
            parts.append(f"also known as {info['nativeName']}")
        if "officialLanguage" in info:
            parts.append(f"where the official language is {info['officialLanguage']}")
        if "language" in info:
            parts.append(f"where the official language is {info['language']}")

        # Construct final sentence
        if parts:
            sentence = f"{subject} {' and '.join(parts)}."
            sentences.append(sentence)

    # Join all sentences into a single coherent paragraph
    return " ".join(sentences)

# EVOLVE-BLOCK-END
