def predict(triples: list[Triple]) -> str:
    sentence = ""
    if not triples:
        return sentence

    subject = triples[0].subject
    combined_info = []

    for triple in triples:
        if triple.predicate == "cityServed":
            combined_info.append(f"serves the city of {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevel":
            combined_info.append(f"is {triple.object} metres above sea level")
        elif triple.predicate == "location":
            combined_info.append(f"is located in {triple.object}")
        elif triple.predicate == "operatingOrganisation":
            combined_info.append(f"is operated by {triple.object}")
        elif triple.predicate == "runwayLength":
            combined_info.append(f"has a runway length of {triple.object} metres")
        elif triple.predicate == "runwayName":
            combined_info.append(f"has a runway named {triple.object}")
        elif triple.predicate == "isPartOf":
            combined_info.append(f"is part of {triple.object}")
        elif triple.predicate == "1stRunwayLengthFeet":
            combined_info.append(f"has a first runway length of {triple.object} feet")
        elif triple.predicate == "1stRunwaySurfaceType":
            combined_info.append(f"has a first runway surface type of {triple.object}")
        elif triple.predicate == "3rdRunwayLengthFeet":
            combined_info.append(f"has a third runway length of {triple.object} feet")
        elif triple.predicate == "icaoLocationIdentifier":
            combined_info.append(f"has ICAO location identifier {triple.object}")
        elif triple.predicate == "locationIdentifier":
            combined_info.append(f"has location identifier {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
            combined_info.append(f"is {triple.object} feet above sea level")
        elif triple.predicate == "iataLocationIdentifier":
            combined_info.append(f"has IATA location identifier {triple.object}")
        elif triple.predicate == "nativeName":
            combined_info.append(f"is also known as {triple.object}")
        elif triple.predicate == "leaderParty":
            combined_info.append(f"is led by the {triple.object}")
        elif triple.predicate == "language":
            combined_info.append(f"speaks {triple.object}")
        elif triple.predicate == "owner":
            combined_info.append(f"is owned by {triple.object}")
        elif triple.predicate == "1stRunwayLengthMetre":
            combined_info.append(f"has a first runway length of {triple.object} metres")
        elif triple.predicate == "4thRunwaySurfaceType":
            combined_info.append(f"has a fourth runway surface type of {triple.object}")
        elif triple.predicate == "largestCity":
            combined_info.append(f"has largest city {triple.object}")
        elif triple.predicate == "4thRunwayLengthFeet":
            combined_info.append(f"has a fourth runway length of {triple.object} feet")
        elif triple.predicate == "elevationAboveTheSeaLevelInMetres":
            combined_info.append(f"is {triple.object} metres above sea level")
        elif triple.predicate == "administrativeArrondissement":
            combined_info.append(f"is in the {triple.object}")
        else:
            combined_info.append(f"is a {triple.predicate}")

    sentence = f"{subject} " + ", ".join(combined_info) + "."
    return sentence

