from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    subject_map = {}
    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if subject not in subject_map:
            subject_map[subject] = []
        subject_map[subject].append((predicate, object_val))

    for subject, relations in subject_map.items():
        sentence += f"{subject} "
        relation_strings = []
        for predicate, object_val in relations:
            if predicate == "cityServed":
                relation_strings.append(f"serves the city of {object_val}")
            elif predicate == "country":
                relation_strings.append(f"is located in {object_val}")
            elif predicate == "capital":
                relation_strings.append(f"has its capital in {object_val}")
            elif predicate == "elevationAboveTheSeaLevel":
                relation_strings.append(f"has an elevation of {object_val} meters")
            elif predicate == "location":
                relation_strings.append(f"is located in {object_val}")
            elif predicate == "operatingOrganisation":
                relation_strings.append(f"is operated by {object_val}")
            elif predicate == "runwayLength":
                relation_strings.append(f"has a runway length of {object_val} meters")
            elif predicate == "runwayName":
                relation_strings.append(f"has a runway named {object_val}")
            elif predicate == "isPartOf":
                relation_strings.append(f"is part of {object_val}")
            elif predicate == "language":
                relation_strings.append(f"speaks {object_val}")
            elif predicate == "officialLanguage":
                relation_strings.append(f"has {object_val} as an official language")
            else:
                relation_strings.append(f"{predicate} {object_val}")

        sentence += ", ".join(relation_strings) + ". "

    return sentence

