from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    location_info = []
    airport_info = []
    other_info = []

    for triple in triples:
        if triple.predicate in ("cityServed", "location", "isPartOf", "administrativeArrondissement"):
            location_info.append(triple)
        elif triple.predicate in ("runwayLength", "runwayName", "1stRunwayLengthFeet", "1stRunwaySurfaceType", "elevationAboveTheSeaLevel", "elevationAboveTheSeaLevelInFeet", "1stRunwayLengthMetre", "country", "officialLanguage", "leaderParty", "language"):
            airport_info.append(triple)
        else:
            other_info.append(triple)

    if location_info:
        subject = location_info[0].subject
        sentence += f"{subject} "
        location_clauses = []
        for triple in location_info:
            if triple.predicate == "cityServed":
                location_clauses.append(f"serves the city of {triple.object}")
            elif triple.predicate == "location":
                location_clauses.append(f"is located in {triple.object}")
            elif triple.predicate == "isPartOf":
                location_clauses.append(f"which is part of {triple.object}")
            elif triple.predicate == "administrativeArrondissement":
                location_clauses.append(f"in the {triple.object}")
        sentence += ", ".join(location_clauses) + ". "

    if airport_info:
        airport_clauses = []
        for triple in airport_info:
            if triple.predicate == "runwayLength":
                airport_clauses.append(f"has a runway length of {triple.object} meters")
            elif triple.predicate == "runwayName":
                airport_clauses.append(f"has a runway named {triple.object}")
            elif triple.predicate == "1stRunwayLengthFeet":
                airport_clauses.append(f"has a first runway length of {triple.object} feet")
            elif triple.predicate == "1stRunwaySurfaceType":
                airport_clauses.append(f"has a first runway surface type of {triple.object}")
            elif triple.predicate == "elevationAboveTheSeaLevel":
                airport_clauses.append(f"has an elevation of {triple.object} meters above sea level")
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                airport_clauses.append(f"has an elevation of {triple.object} feet above sea level")
            elif triple.predicate == "1stRunwayLengthMetre":
                airport_clauses.append(f"has a first runway length of {triple.object} meters")
            elif triple.predicate == "country":
                airport_clauses.append(f"is located in {triple.object}")
            elif triple.predicate == "officialLanguage":
                airport_clauses.append(f"where the official language is {triple.object}")
            elif triple.predicate == "leaderParty":
                airport_clauses.append(f"and is led by the {triple.object}")
            elif triple.predicate == "language":
                airport_clauses.append(f"where the language is {triple.object}")

        sentence += ", ".join(airport_clauses) + ". "

    for triple in other_info:
        sentence += f"{triple.subject} {triple.predicate} {triple.object}. "

    return sentence

