        elif predicate == "locationIdentifier":
            clauses.append(f"with location identifier {object_val}")
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            clauses.append(f"with an elevation of {object_val} feet")
        elif predicate == "iataLocationIdentifier":
            clauses.append(f"with IATA identifier {object_val}")
        elif predicate == "nativeName":
            clauses.append(f"also known as {object_val}")
        elif predicate == "leaderParty":
            clauses.append(f"led by {object_val}")
        elif predicate == "language":
            clauses.append(f"where the official language is {object_val}")
