from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Simple approach: process triples sequentially and build meaningful connections
    sentences = []

    # Keep track of what we've processed to avoid duplicates
    processed_triples = set()

    # Process triples to build sentences
    for triple in triples:
        if triple in processed_triples:
            continue

        processed_triples.add(triple)

        # Special handling for cityServed relationships
        if triple.predicate == "cityServed":
            sentence_parts = [f"{triple.subject} serves the city of {triple.object}"]

            # Look for related triples to add more context
            for related_triple in triples:
                if related_triple.subject == triple.subject and related_triple != triple:
                    if related_triple.predicate == "country":
                        sentence_parts.append(f", which is in {related_triple.object}")
                    elif related_triple.predicate == "location":
                        sentence_parts.append(f" located in {related_triple.object}")
                    elif related_triple.predicate == "elevationAboveTheSeaLevel":
                        sentence_parts.append(f" with an elevation of {related_triple.object} meters above sea level")
                    elif related_triple.predicate == "operatingOrganisation":
                        sentence_parts.append(f", operated by {related_triple.object}")

            sentence = "".join(sentence_parts)
            if not sentence.endswith("."):
                sentence += "."
            sentences.append(sentence)

        # Handle country relationships
        elif triple.predicate == "country":
            sentence_parts = [f"{triple.subject} is in {triple.object}"]

            # Look for related triples
            for related_triple in triples:
                if related_triple.subject == triple.subject and related_triple != triple:
                    if related_triple.predicate == "capital":
                        sentence_parts.append(f", where the capital is {related_triple.object}")
                    elif related_triple.predicate == "officialLanguage":
                        sentence_parts.append(f" and the official language is {related_triple.object}")

            sentence = "".join(sentence_parts)
            if not sentence.endswith("."):
                sentence += "."
            sentences.append(sentence)

        # Handle elevation
        elif triple.predicate == "elevationAboveTheSeaLevel":
            sentence_parts = [f"{triple.subject} has an elevation of {triple.object} meters above sea level"]

            # Add related info
            for related_triple in triples:
                if related_triple.subject == triple.subject and related_triple != triple:
                    if related_triple.predicate == "country":
                        sentence_parts.append(f", located in {related_triple.object}")
                    elif related_triple.predicate == "location":
                        sentence_parts.append(f" in {related_triple.object}")

            sentence = "".join(sentence_parts)
            if not sentence.endswith("."):
                sentence += "."
            sentences.append(sentence)

        # Handle location
        elif triple.predicate == "location":
            sentence_parts = [f"{triple.subject} is located in {triple.object}"]

            # Add related info
            for related_triple in triples:
                if related_triple.subject == triple.subject and related_triple != triple:
                    if related_triple.predicate == "country":
                        sentence_parts.append(f", in {related_triple.object}")

            sentence = "".join(sentence_parts)
            if not sentence.endswith("."):
                sentence += "."
            sentences.append(sentence)

        # For other predicates, simple direct sentences
        else:
            sentence_parts = [f"{triple.subject} {triple.predicate} {triple.object}"]
            sentence = "".join(sentence_parts)
            if not sentence.endswith("."):
                sentence += "."
            sentences.append(sentence)

    # Join sentences with appropriate spacing
    result = " ".join(sentences)
    if result and not result.endswith("."):
        result += "."
    return result

