from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    location_info = []
    airport_info = []
    other_info = []

    for triple in triples:
        if triple.predicate in ("cityServed", "location", "isPartOf", "administrativeArrondissement", "country", "capital"):
            location_info.append(triple)
        elif triple.predicate in ("runwayLength", "runwayName", "1stRunwayLengthFeet", "1stRunwaySurfaceType", "elevationAboveTheSeaLevel", "elevationAboveTheSeaLevelInFeet", "1stRunwayLengthMetre", "1stRunwayNumber", "2ndRunwaySurfaceType", "3rdRunwaySurfaceType", "4thRunwayLengthFeet", "4thRunwaySurfaceType", "5thRunwayNumber", "5thRunwaySurfaceType"):
            airport_info.append(triple)
        else:
            other_info.append(triple)

    if location_info:
        subject = location_info[0].subject
        sentence += f"{subject} "
        location_clauses = []
        for triple in location_info:
            if triple.predicate == "cityServed":
                location_clauses.append(f"serves the city of {triple.object}")
            elif triple.predicate == "location":
                location_clauses.append(f"is located in {triple.object}")
            elif triple.predicate == "isPartOf":
                location_clauses.append(f"which is part of {triple.object}")
            elif triple.predicate == "administrativeArrondissement":
                location_clauses.append(f"in the {triple.object}")
            elif triple.predicate == "country":
                location_clauses.append(f"is in {triple.object}")
            elif triple.predicate == "capital":
                location_clauses.append(f"where the capital is {triple.object}")
        sentence += ", ".join(location_clauses) + ". "

    if airport_info:
        for triple in airport_info:
            if triple.predicate == "runwayLength":
                sentence += f"The runway length of {triple.subject} is {triple.object} meters. "
            elif triple.predicate == "runwayName":
                sentence += f"The runway name of {triple.subject} is {triple.object}. "
            elif triple.predicate == "1stRunwayLengthFeet":
                sentence += f"The first runway of {triple.subject} is {triple.object} feet long. "
            elif triple.predicate == "1stRunwaySurfaceType":
                sentence += f"The first runway surface type of {triple.subject} is {triple.object}. "
            elif triple.predicate == "elevationAboveTheSeaLevel":
                sentence += f"{triple.subject} has an elevation of {triple.object} meters above sea level. "
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                sentence += f"{triple.subject} has an elevation of {triple.object} feet above sea level. "
            elif triple.predicate == "1stRunwayLengthMetre":
                sentence += f"The first runway of {triple.subject} is {triple.object} meters long. "
            elif triple.predicate == "1stRunwayNumber":
                sentence += f"Runway {triple.object} is at {triple.subject}. "
            elif triple.predicate == "2ndRunwaySurfaceType":
                sentence += f"The second runway surface type of {triple.subject} is {triple.object}. "
            elif triple.predicate == "3rdRunwaySurfaceType":
                sentence += f"The third runway surface type of {triple.subject} is {triple.object}. "
            elif triple.predicate == "4thRunwayLengthFeet":
                sentence += f"The fourth runway of {triple.subject} is {triple.object} feet long. "
            elif triple.predicate == "4thRunwaySurfaceType":
                sentence += f"The fourth runway surface type of {triple.subject} is {triple.object}. "
            elif triple.predicate == "5thRunwayNumber":
                sentence += f"Runway {triple.object} is at {triple.subject}. "
            elif triple.predicate == "5thRunwaySurfaceType":
                sentence += f"The fifth runway surface type of {triple.subject} is {triple.object}. "

    for triple in other_info:
        sentence += f"{triple.subject} {triple.predicate} {triple.object}. "

    return sentence

