    sentence = ""
    if not triples:
        return sentence

    subject = triples[0].subject
    sentence += f"{subject} "

    properties = []
    for triple in triples:
        if triple.predicate == "cityServed":
            properties.append(f"serves the city of {triple.object}")
        elif triple.predicate == "country":
            properties.append(f"is located in {triple.object}")
        elif triple.predicate == "capital":
            properties.append(f"where the capital is {triple.object}")
