            properties.append(f"has a runway length of {triple.object} meters")
        elif triple.predicate == "runwayName":
            properties.append(f"has a runway named {triple.object}")
        elif triple.predicate == "isPartOf":
            properties.append(f"is part of {triple.object}")
        elif triple.predicate == "1stRunwayLengthFeet":
            properties.append(f"has a first runway length of {triple.object} feet")
        elif triple.predicate == "1stRunwaySurfaceType":
            properties.append(f"has a first runway surface of {triple.object}")
