        elif triple.predicate == "iataLocationIdentifier":
            properties.append(f"has IATA identifier {triple.object}")
        elif triple.predicate == "nativeName":
            properties.append(f"is also known as {triple.object}")
        elif triple.predicate == "leaderParty":
            properties.append(f"is led by {triple.object}")
        elif triple.predicate == "language":
            properties.append(f"speaks {triple.object}")
