                relation_strings.append(f"is located in {object_val}")
            elif predicate == "operatingOrganisation":
                relation_strings.append(f"is operated by {object_val}")
            elif predicate == "runwayLength":
                relation_strings.append(f"has a runway length of {object_val} meters")
            elif predicate == "runwayName":
                relation_strings.append(f"has a runway name of {object_val}")
            elif predicate == "isPartOf":
                relation_strings.append(f"is part of {object_val}")
            elif predicate == "1stRunwayLengthFeet":
                relation_strings.append(f"has a first runway length of {object_val} feet")
            elif predicate == "1stRunwaySurfaceType":
                relation_strings.append(f"has a first runway surface type of {object_val}")
            elif predicate == "3rdRunwayLengthFeet":
                relation_strings.append(f"has a third runway length of {object_val} feet")
            elif predicate == "icaoLocationIdentifier":
