            elif predicate == "locationIdentifier":
                relation_strings.append(f"has a location identifier of {object_val}")
            elif predicate == "elevationAboveTheSeaLevelInFeet":
                relation_strings.append(f"has an elevation of {object_val} feet")
            elif predicate == "iataLocationIdentifier":
                relation_strings.append(f"has an IATA identifier of {object_val}")
            elif predicate == "nativeName":
                relation_strings.append(f"is also known as {object_val}")
            elif predicate == "leaderParty":
                relation_strings.append(f"is led by {object_val}")
            elif predicate == "language":
                relation_strings.append(f"has the language {object_val}")
