        for sentence in sentences:
            # Extract subject from the beginning of the sentence
            subject = sentence.split()[0]
            if subject not in subject_facts:
                subject_facts[subject] = []
            subject_facts[subject].append(sentence)

        complex_sentences = []
        for subject, facts in subject_facts.items():
