            if len(facts) == 1:
                complex_sentences.append(facts[0])
            elif len(facts) == 2:
                complex_sentences.append(f"{facts[0]} and {facts[1]}")
            else:
                # Use serial comma (Oxford comma) for three or more facts
                fact_string = ", ".join(facts[:-1]) + ", and " + facts[-1]
