from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group related facts by subject to create longer, more coherent sentences
    from collections import defaultdict
    subject_facts = defaultdict(list)

    for triple in triples:
        subject_facts[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, facts in subject_facts.items():
        # Organize facts by semantic categories for better flow
        birth_info = []
        career_info = []
        origin_info = []
        music_info = []
        other_info = []

        for pred, obj in facts:
            if pred in ("birthDate", "birthPlace", "birthYear"):
                birth_info.append(obj)
            elif pred in ("activeYearsStartYear", "activeYearsEndYear"):
                career_info.append(obj)
            elif pred in ("origin", "nationality", "country", "isPartOf"):
                origin_info.append(obj)
            elif pred in ("genre", "musicSubgenre", "musicFusionGenre", "stylisticOrigin", "associatedBand/associatedMusicalArtist"):
                music_info.append(obj)
            else:
                other_info.append(obj)

        # Build sentence components
        parts = []
        if birth_info:
            birth_str = ", ".join(birth_info)
            parts.append(f"born on {birth_str}" if "birthDate" in [p for p, _ in facts] else f"born in {birth_str}")
        if career_info:
            career_str = "from " + career_info[0] if "activeYearsStartYear" in [p for p, _ in facts] else ""
            if "activeYearsEndYear" in [p for p, _ in facts]:
                career_str += f" to {career_info[1]}" if len(career_info) > 1 else ""
            if career_str:
                parts.append(career_str)
        if origin_info:
            origin_str = ", ".join(origin_info)
            parts.append(f"from {origin_str}")
        if music_info:
            music_str = ", ".join(music_info)
            parts.append(f"performing {music_str} music")
        if other_info:
            # Handle remaining facts as a general list
            parts.append(f"also known for {', '.join(other_info)}")

        # Combine all parts into a single sentence
        sentence = f"{subject} {' and '.join(parts)}."
        sentences.append(sentence)

    return " ".join(sentences)

