from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    sentences = []

    # Group triples by subject
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    # Generate sentences for each subject group
    for subject, triples in subject_groups.items():
        # Find the most common predicate
        predicates = [triple.predicate for triple in triples]
        most_common_predicate = max(set(predicates), key=predicates.count)

        # Generate sentence based on the most common predicate
        if most_common_predicate == "associatedBand/associatedMusicalArtist":
            sentence = f"{subject} is associated with {triples[0].object}."
        elif most_common_predicate == "background":
            sentence = f"{subject} has a background as a {triples[0].object}."
        elif most_common_predicate == "birthDate":
            sentence = f"{subject} was born on {triples[0].object}."
        # ... (other predicate handlers)
        else:
            sentence = f"{subject} {most_common_predicate} {triples[0].object}."
        sentences.append(sentence)

    if len(sentences) == 1:
        return sentences[0]
    else:
        return ". ".join(sentences)

