from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to enable richer, more natural compound sentences
    subject_groups = {}
    for triple in triples:
        subject = triple.subject
        if subject not in subject_groups:
            subject_groups[subject] = []
        subject_groups[subject].append(triple)

    sentences = []
    for subject, triples_list in subject_groups.items():
        # Build a detailed description for each subject
        parts = []
        birth_info = None
        origin_info = None
        genre_info = None
        instrument_info = None
        active_years = None
        record_label = None
        occupation = None
        nationality = None
        associated_bands = []
        background = None

        for triple in triples_list:
            predicate = triple.predicate
            obj = triple.object

            if predicate == "birthDate":
                birth_info = f"born on {obj}"
            elif predicate == "birthPlace":
                birth_info = f"born in {obj}"
            elif predicate == "birthYear":
                birth_info = f"born in {obj}"
            elif predicate == "origin":
                origin_info = f"from {obj}"
            elif predicate == "genre":
                genre_info = f"plays {obj} music"
            elif predicate == "instrument":
                instrument_info = f"plays the {obj}"
            elif predicate == "activeYearsStartYear":
                active_years = f"active since {obj}"
            elif predicate == "activeYearsEndYear":
                active_years = f"active until {obj}"
            elif predicate == "recordLabel":
                record_label = f"signed to {obj}"
            elif predicate == "occupation":
                occupation = f"a {obj}"
            elif predicate == "nationality":
                nationality = f"from {obj}"
            elif predicate == "associatedBand/associatedMusicalArtist":
                associated_bands.append(obj)
            elif predicate == "background":
                background = f"with a background as a {obj}"

        # Build a rich sentence using collected facts with better grammar and flow
        parts = []
        if birth_info:
            parts.append(birth_info)
        if origin_info:
            parts.append(origin_info)
        if nationality:
            parts.append(nationality)
        if background:
            parts.append(background)
        if genre_info:
            parts.append(genre_info)
        if instrument_info:
            parts.append(instrument_info)
        if active_years:
            parts.append(active_years)
        if record_label:
            parts.append(record_label)
        if occupation:
            parts.append(occupation)
        if associated_bands:
            bands_str = ", ".join(associated_bands)
            parts.append(f"associated with {bands_str}")

        # Construct a natural-sounding sentence with proper conjunctions
        if len(parts) == 1:
            base = f"{subject} {parts[0]}."
        else:
            # Use commas and 'and' for the last item
            base = f"{subject} {', '.join(parts[:-1])} and {parts[-1]}."
        sentences.append(base)

    # Combine all sentences into a single coherent statement
    return " ".join(sentences)

