def predict(triples: list[Triple]) -> str:
    """
    Generates a natural language sentence from a list of triples.
    """
    if not triples:
        return ""

    sentences = []
    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "associatedBand/associatedMusicalArtist":
            sentences.append(f"{subject} is associated with {object_val}.")
        elif predicate == "background":
            sentences.append(f"{subject} has a background as a {object_val}.")
        elif predicate == "birthDate":
            sentences.append(f"{subject} was born on {object_val}.")
        elif predicate == "genre":
            sentences.append(f"{subject} performs {object_val} music.")
        elif predicate == "instrument":
            sentences.append(f"{subject} plays the {object_val}.")
        elif predicate == "origin":
            sentences.append(f"{subject} is from {object_val}.")
        elif predicate == "activeYearsStartYear":
            sentences.append(f"{subject}'s active years began in {object_val}.")
        elif predicate == "birthPlace":
            sentences.append(f"{subject} was born in {object_val}.")
        elif predicate == "birthYear":
            sentences.append(f"{subject} was born in {object_val}.")
        elif predicate == "occupation":
            sentences.append(f"{subject} works as a {object_val}.")
        elif predicate == "recordLabel":
            sentences.append(f"{subject} is signed to {object_val}.")
        elif predicate == "deathPlace":
            sentences.append(f"{subject} died in {object_val}.")
        elif predicate == "alternativeName":
            sentences.append(f"{subject} is also known as {object_val}.")
        elif predicate == "activeYearsEndYear":
            sentences.append(f"{subject}'s active years ended in {object_val}.")
        elif predicate == "deathDate":
            sentences.append(f"{subject} died on {object_val}.")
        elif predicate == "nationality":
            sentences.append(f"{subject} is from {object_val}.")
        elif predicate == "professionalField":
            sentences.append(f"{subject} works in the field of {object_val}.")
        elif predicate == "musicFusionGenre":
            sentences.append(f"{object_val} is a fusion genre of {subject}.")
        elif predicate == "musicSubgenre":
            sentences.append(f"{subject} is a subgenre of {object_val}.")
        elif predicate == "stylisticOrigin":
            sentences.append(f"{subject} originated from {object_val}.")
        elif predicate == "language":
            sentences.append(f"{subject} is spoken in {object_val}.")
        elif predicate == "officialLanguage":
            sentences.append(f"{object_val} is an official language of {subject}.")
        elif predicate == "derivative":
            sentences.append(f"{subject} is a derivative of {object_val}.")
        elif predicate == "location":
            sentences.append(f"{subject} is located in {object_val}.")
        elif predicate == "governingBody":
            sentences.append(f"{subject} is governed by {object_val}.")
        elif predicate == "leader":
            sentences.append(f"{subject}'s leader is {object_val}.")
        elif predicate == "leaderTitle":
            sentences.append(f"{subject} holds the title of {object_val}.")
        elif predicate == "country":
            sentences.append(f"{subject} is a city in {object_val}.")
        elif predicate == "ethnicGroup":
            sentences.append(f"{subject} is inhabited by the {object_val} ethnic group.")
        elif predicate == "parentCompany":
            sentences.append(f"{subject} is a subsidiary of {object_val}.")
        elif predicate == "bandMember":
            sentences.append(f"{subject} features {object_val} as a member.")
        elif predicate == "capital":
            sentences.append(f"{subject}'s capital is {object_val}.")
        elif predicate == "anthem":
            sentences.append(f"{subject}'s anthem is {object_val}.")
        elif predicate == "currency":
            sentences.append(f"{subject}'s currency is {object_val}.")
        elif predicate == "demonym":
            sentences.append(f"People from {subject} are called {object_val}.")
        elif predicate == "isPartOf":
            sentences.append(f"{subject} is part of {object_val}.")
        elif predicate == "distributingCompany":
            sentences.append(f"{subject} is distributed by {object_val}.")
        elif predicate == "training":
            sentences.append(f"{subject} received training at {object_val}.")
        elif predicate == "populationDensity":
            sentences.append(f"{subject}'s population density is {object_val}.")
        elif predicate == "meaning":
            sentences.append(f"{subject} means {object_val}.")
        elif predicate == "longName":
            sentences.append(f"{subject}'s long name is {object_val}.")
        elif predicate == "areaTotal":
            sentences.append(f"{subject}'s total area is {object_val}.")
        elif predicate == "foundingDate":
            sentences.append(f"{subject} was founded on {object_val}.")
        elif predicate == "elevationAboveTheSeaLevel":
            sentences.append(f"{subject}'s elevation above sea level is {object_val}.")
        elif predicate == "postalCode":
            sentences.append(f"{subject}'s postal code is {object_val}.")
        else:
            sentences.append(f"The {predicate} of {subject} is {object_val}.")

    # Group triples by subject to build coherent, multi-fact sentences
    from collections import defaultdict
    subject_groups = defaultdict(list)
    for triple in triples:
        subject_groups[triple.subject].append(triple)

    sentences = []
    for subject, group in subject_groups.items():
        facts = []
        birth_info = []
        career_info = []
        music_info = []
        affiliation_info = []
        location_info = []
        other_info = []

        for triple in group:
            predicate = triple.predicate
            object_val = triple.object

            if predicate in ["birthDate", "birthPlace", "birthYear"]:
                birth_info.append(object_val)
            elif predicate in ["activeYearsStartYear", "activeYearsEndYear"]:
                career_info.append(object_val)
            elif predicate in ["genre", "instrument", "background", "musicSubgenre", "musicFusionGenre", "stylisticOrigin"]:
                music_info.append(object_val)
            elif predicate in ["recordLabel", "associatedBand/associatedMusicalArtist", "bandMember", "distributingCompany", "parentCompany"]:
                affiliation_info.append(object_val)
            elif predicate in ["country", "capital", "location", "isPartOf", "postalCode", "areaTotal", "elevationAboveTheSeaLevel"]:
                location_info.append(object_val)
            elif predicate in ["nationality", "occupation", "professionalField", "alternativeName", "demonym", "ethnicGroup"]:
                other_info.append(object_val)
            elif predicate in ["deathDate", "deathPlace"]:
                # Handle death info separately as it may be part of a narrative
                death_info = f"and died on {object_val}" if predicate == "deathDate" else f"and died in {object_val}"
                facts.append(death_info)
                continue
            else:
                other_info.append(object_val)

        # Construct natural narrative flow: birth → career → music → affiliations → location → other
        if birth_info:
            birth_str = ""
            if len(birth_info) == 1:
                birth_str = f"born on {birth_info[0]}" if "date" in predicate else f"born in {birth_info[0]}"
            elif len(birth_info) == 2:
                birth_str = f"born on {birth_info[0]} in {birth_info[1]}"
            elif len(birth_info) == 3:
                birth_str = f"born on {birth_info[0]} in {birth_info[1]} in {birth_info[2]}"
            else:
                birth_str = f"born in {', '.join(birth_info)}"
            facts.append(f"{subject} was {birth_str}")

        if career_info:
            start_year = next((y for y in career_info if "start" in y), None)
            end_year = next((y for y in career_info if "end" in y), None)
            if start_year and end_year:
                facts.append(f"active from {start_year} to {end_year}")
            elif start_year:
                facts.append(f"active since {start_year}")
            elif end_year:
                facts.append(f"active until {end_year}")

        if music_info:
            genre = next((m for m in music_info if "genre" in m), None)
            instrument = next((m for m in music_info if "instrument" in m), None)
            background = next((m for m in music_info if "background" in m), None)
            if genre:
                facts.append(f"performing {genre} music")
            if instrument:
                facts.append(f"playing the {instrument}")
            if background:
                facts.append(f"with a background as a {background}")

        if affiliation_info:
            labels = []
            for af in affiliation_info:
                if "recordLabel" in af or "distributingCompany" in af:
                    labels.append(f"signed to {af}")
                elif "band" in af:
                    labels.append(f"member of {af}")
                elif "parentCompany" in af:
                    labels.append(f"subsidiary of {af}")
                else:
                    labels.append(af)
            if labels:
                facts.append(f"affiliated with {', '.join(labels)}")

        if location_info:
            locations = []
            for loc in location_info:
                if "country" in loc or "capital" in loc:
                    locations.append(f"in {loc}")
                else:
                    locations.append(loc)
            if locations:
                facts.append(f"located in {', '.join(locations)}")

        if other_info:
            others = []
            for o in other_info:
                if "nationality" in o or "occupation" in o:
                    others.append(o)
                elif "alternativeName" in o:
                    others.append(f"also known as {o}")
                elif "demonym" in o:
                    others.append(f"people from here are called {o}")
                elif "ethnicGroup" in o:
                    others.append(f"inhabited by the {o} ethnic group")
                else:
                    others.append(o)
            if others:
                facts.append(f"{', '.join(others)}")

        if facts:
            sentence = f"{subject} is {', '.join(facts)}."
            sentences.append(sentence)

    return " ".join(sentences)

