from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to create richer, more natural compound sentences
    subject_groups = {}
    for triple in triples:
        subject = triple.subject
        if subject not in subject_groups:
            subject_groups[subject] = []
        subject_groups[subject].append(triple)

    sentences = []
    for subject, triples_list in subject_groups.items():
        parts = []
        birth_info = None
        origin_info = None
        genre_info = None
        instrument_info = None
        active_years = None
        record_label = None
        occupation = None
        nationality = None
        associated_bands = []
        background = None

        for triple in triples_list:
            predicate = triple.predicate
            obj = triple.object

            if predicate == "birthDate":
                birth_info = f"born on {obj}"
            elif predicate == "birthPlace":
                birth_info = f"born in {obj}"
            elif predicate == "birthYear":
                birth_info = f"born in {obj}"
            elif predicate == "origin":
                origin_info = f"from {obj}"
            elif predicate == "genre":
                genre_info = f"plays {obj} music"
            elif predicate == "instrument":
                instrument_info = f"plays the {obj}"
            elif predicate == "activeYearsStartYear":
                active_years = f"active since {obj}"
            elif predicate == "activeYearsEndYear":
                active_years = f"active until {obj}"
            elif predicate == "recordLabel":
                record_label = f"signed to {obj}"
            elif predicate == "occupation":
                occupation = f"a {obj}"
            elif predicate == "nationality":
                nationality = f"from {obj}"
            elif predicate == "associatedBand/associatedMusicalArtist":
                associated_bands.append(obj)
            elif predicate == "background":
                background = f"with a background as a {obj}"

        # Build a rich sentence using collected facts
        base = subject
        if birth_info:
            base += f" {birth_info}"
        if origin_info:
            base += f" {origin_info}"
        if nationality:
            base += f" {nationality}"
        if background:
            base += f", {background}"
        if genre_info:
            base += f", {genre_info}"
        if instrument_info:
            base += f", {instrument_info}"
        if active_years:
            base += f", {active_years}"
        if record_label:
            base += f", {record_label}"
        if occupation:
            base += f", {occupation}"
        if associated_bands:
            bands_str = ", ".join(associated_bands)
            base += f", associated with {bands_str}"

        sentences.append(base + ".")

    # Combine all sentences into a single coherent statement to improve sentence length score
    return " ".join(sentences)

