from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group related facts by subject to create longer, more coherent sentences
    from collections import defaultdict
    subject_facts = defaultdict(list)

    for triple in triples:
        subject_facts[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, facts in subject_facts.items():
        # Organize facts by semantic categories for better flow
        birth_info = []
        career_info = []
        origin_info = []
        music_info = []
        other_info = []

        for pred, obj in facts:
            if pred in ("birthDate", "birthPlace", "birthYear"):
                birth_info.append(obj)
            elif pred in ("activeYearsStartYear", "activeYearsEndYear"):
                career_info.append(obj)
            elif pred in ("origin", "nationality", "country", "isPartOf"):
                origin_info.append(obj)
            elif pred in ("genre", "musicSubgenre", "musicFusionGenre", "stylisticOrigin", "associatedBand/associatedMusicalArtist"):
                music_info.append(obj)
            else:
                other_info.append(obj)

        # Build sentence components with improved logic and grammar
        parts = []
        if birth_info:
            birth_str = ", ".join(birth_info)
            if "birthDate" in [p for p, _ in facts]:
                parts.append(f"born on {birth_str}")
            elif "birthPlace" in [p for p, _ in facts]:
                parts.append(f"born in {birth_str}")
            else:
                parts.append(f"born in {birth_str}")
        if career_info:
            start_year = career_info[0] if len(career_info) > 0 else ""
            end_year = career_info[1] if len(career_info) > 1 else ""
            if start_year and end_year:
                parts.append(f"active from {start_year} to {end_year}")
            elif start_year:
                parts.append(f"active from {start_year}")
            elif end_year:
                parts.append(f"active until {end_year}")
        if origin_info:
            origin_str = ", ".join(origin_info)
            parts.append(f"from {origin_str}")
        if music_info:
            music_str = ", ".join(music_info)
            parts.append(f"performing {music_str} music")
        if other_info:
            # Handle remaining facts as a general list with proper phrasing
            parts.append(f"also known for {', '.join(other_info)}")

        # Combine all parts into a single sentence with better conjunctions
        sentence = f"{subject} {' and '.join(parts)}."
        sentences.append(sentence)

    # Join sentences with proper punctuation and flow
    if len(sentences) == 1:
        return sentences[0]
    elif len(sentences) == 2:
        return " and ".join(sentences)
    elif len(sentences) == 3:
        return ", ".join(sentences[:-1]) + ", and " + sentences[-1]
    else:
        return " ".join(sentences)

