from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to combine sentences
    triples_by_subject = {}
    for triple in triples:
        triples_by_subject.setdefault(triple.subject, []).append(triple)

    sentences = []
    for subject, triples in triples_by_subject.items():
        predicates = set()
        objects = set()
        for triple in triples:
            predicates.add(triple.predicate)
            objects.add(triple.object)

        # Combine sentences with common subjects and predicates/objects
        if len(predicates) == 1 and len(objects) == 1:
            predicate, object_ = next(iter(predicates)), next(iter(objects))
            sentence = f"{subject} {predicate.replace('/', ' ')} {object_}."
            sentences.append(sentence)
        else:
            for predicate in predicates:
                for object_ in objects:
                    sentence = f"{subject} {predicate.replace('/', ' ')} {object_}."
                    sentences.append(sentence)

    # Join sentences with appropriate conjunctions
    if len(sentences) == 1:
        return sentences[0] + "."
    elif len(sentences) == 2:
        return f"{sentences[0]} and {sentences[1]}."
    elif len(sentences) == 3:
        return f"{sentences[0]}, {sentences[1]}, and {sentences[2]}."
    else:
        return " ".join(sentences) + "."

