                ordered.extend(affiliation_facts)
                ordered.extend(location_facts)
                ordered.extend(other_facts)

                # For multiple facts about same subject, combine them naturally
                if len(ordered) == 1:
                    compound_sentences.append(ordered[0])
                else:
                    # Create more complex sentences with relative clauses and conjunctions
                    combined = ordered[0].rstrip('.').rstrip(',')
                    for i, fact in enumerate(ordered[1:], 1):
