from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    sentences = []
    current_sentence = ""
    for triple in triples:
        if triple.predicate == "associatedBand/associatedMusicalArtist":
            if current_sentence:
                current_sentence += f" and {triple.subject} is associated with {triple.object}."
            else:
                current_sentence = f"{triple.subject} is associated with {triple.object}."
        elif triple.predicate == "background":
            if current_sentence:
                current_sentence += f" {triple.subject} has a background as a {triple.object}."
            else:
                current_sentence = f"{triple.subject} has a background as a {triple.object}."
        elif triple.predicate == "birthDate":
            if current_sentence:
                current_sentence += f" {triple.subject} was born on {triple.object}."
            else:
                current_sentence = f"{triple.subject} was born on {triple.object}."
        elif triple.predicate == "genre":
            if current_sentence:
                current_sentence += f" {triple.subject} performs {triple.object} music."
            else:
                current_sentence = f"{triple.subject} performs {triple.object} music."
        # Add similar logic for other predicates to combine into a single sentence.
        else:
            if current_sentence:
                current_sentence += f" The {triple.predicate} of {triple.subject} is {triple.object}."
            else:
                current_sentence = f"The {triple.predicate} of {triple.subject} is {triple.object}."

    sentences.append(current_sentence)
    return " ".join(sentences)

