def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    combined_sentence = ""
    for triple in triples:
        predicate = triple.predicate
        subject = triple.subject
        object_ = triple.object

        if predicate == "associatedBand/associatedMusicalArtist":
            combined_sentence += f"{subject} is associated with {object_}. "
        elif predicate == "background":
            combined_sentence += f"{subject} has a background as a {object_}. "
        elif predicate == "birthDate":
            combined_sentence += f"{subject} was born on {object_}. "
        elif predicate == "genre":
            combined_sentence += f"{subject} performs {object_} music. "
        elif predicate == "instrument":
            combined_sentence += f"{subject} plays the {object_}. "
        elif predicate == "origin":
            combined_sentence += f"{subject} originates from {object_}. "
        elif predicate == "activeYearsStartYear":
            combined_sentence += f"{subject} became active in {object_}. "
        elif predicate == "birthPlace":
            combined_sentence += f"{subject} was born in {object_}. "
        elif predicate == "birthYear":
            combined_sentence += f"{subject} was born in {object_}. "
        elif predicate == "occupation":
            combined_sentence += f"{subject} works as a {object_}. "
        elif predicate == "recordLabel":
            combined_sentence += f"{subject} is signed to {object_}. "
        elif predicate == "deathPlace":
            combined_sentence += f"{subject} died in {object_}. "
        elif predicate == "alternativeName":
            combined_sentence += f"{subject} is also known as {object_}. "
        elif predicate == "activeYearsEndYear":
            combined_sentence += f"{subject} was active until {object_}. "
        elif predicate == "deathDate":
