from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to create more coherent, subject-focused sentences
    from collections import defaultdict
    subject_facts = defaultdict(list)

    for triple in triples:
        subject_facts[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, facts in subject_facts.items():
        # Categorize facts for better sentence structure
        birth_facts = []
        career_facts = []
        origin_facts = []
        music_facts = []
        other_facts = []

        for pred, obj in facts:
            if pred in ("birthDate", "birthPlace", "birthYear"):
                birth_facts.append(obj)
            elif pred in ("activeYearsStartYear", "activeYearsEndYear"):
                career_facts.append(obj)
            elif pred in ("origin", "nationality", "country", "isPartOf"):
                origin_facts.append(obj)
            elif pred in ("genre", "musicSubgenre", "musicFusionGenre", "stylisticOrigin", "associatedBand/associatedMusicalArtist"):
                music_facts.append(obj)
            else:
                other_facts.append(obj)

        # Build natural language components
        components = []

        # Birth info
        if birth_facts:
            birth_str = " and ".join(birth_facts)
            if any(p in ("birthDate", "birthYear") for p, _ in facts):
                components.append(f"born on {birth_str}" if "birthDate" in [p for p, _ in facts] else f"born in {birth_str}")
            else:
                components.append(f"born in {birth_str}")

        # Career info
        if career_facts:
            start_year = career_facts[0] if len(career_facts) > 0 else ""
            end_year = career_facts[1] if len(career_facts) > 1 else ""
            career_str = f"active from {start_year}"
            if end_year:
                career_str += f" to {end_year}"
            components.append(career_str)

        # Origin info
        if origin_facts:
            origin_str = ", ".join(origin_facts)
            components.append(f"from {origin_str}")

        # Music info
        if music_facts:
            music_str = ", ".join(music_facts)
            components.append(f"performing {music_str} music")

        # Other info
        if other_facts:
            other_str = ", ".join(other_facts)
            components.append(f"also known for {other_str}")

        # Construct final sentence for this subject
        sentence = f"{subject} {' and '.join(components)}."
        sentences.append(sentence)

    # Join all subject sentences into one coherent text
    return " ".join(sentences)

# EVOLVE-BLOCK-END
